pragma solidity >=0.4.25 <0.7.0;

contract CleanEnergyVersion1 {

  //// enhanced code comment 
 
  // CleanEnery to be sold on P2P Energy Network
  struct EnergyProvision {
    string energyType; //type of Renewable Energy
    uint   energyUnit; //qty of energy unit available and to be sold
    bool isActive; // min 50 inuts of clean energy is required to be active on portal to sell
    uint256 unitprice; // 8*10^13wei(INR2) is the set unit price for first 50 units (once crosses 100 units of purchase at a time, the price/unit is 4*10^13wei)
    address payable prosumer; // producer/seller of the CleanEnergy
    uint256 unitbalance;// unit balance available after any point of selling
  }

  //declaring the energyid with an assigned value
  uint256 public energyId = 1;

  // mapping of energyId to EnergyType object
  mapping(uint256 => EnergyProvision) public availableEnergy;
  
  //user/buyer/consumer registration
  
  struct Consumer {
      uint256 consumerid;
      string name;
      address consumer;
      string location;
      string mobile;
      string email;
      bool isProsumer;
      
  }
  //mapping address to balance
  mapping (address => uint) public balances;
  
  //mapping address of consumer to struct Details
  
  mapping(address=>Consumer) public consumers;
  
  uint256 public consumerid = 1000;
  //returning a consunerid in Dapp after successful registration
  event returnconsumerid(uint256 id);
  
  function ConsumerRegistration(string calldata _name, string calldata _location, string calldata _mobile, string calldata _email, bool _isprosumer) external  {
      Consumer memory newConsumer = Consumer(consumerid, _name, msg.sender, _location, _mobile, _email, _isprosumer);
      consumers[msg.sender] = newConsumer;
      emit returnconsumerid(consumerid++);
  }
  

  // Details of a energy selling
  struct Billing {
    uint256 energyId;
    uint256 energyUnit;//energy going to Consume from the prosumer
    address consumer;//buyer
  }

  uint256 public BillingId = 1000;

  // mapping of energyId to Billing object
  mapping(uint256 => Billing) public billingDetails;

  // This event is emitted when a new EnergyQty is put up for sale
  event NewEnergy (
    uint256 indexed energyId
  );

  // This event is emitted when a NewBilling is made
  event NewBilling (
    uint256 indexed energyId,
    uint256 indexed billingId
  );
  
  
  // creating an event which to emit the details of the CleanEngery availablity by the prosumer
  event getEnergyDetails(uint energyIdId,string energyType, uint energyUnit , address prosumer);
  
  // creating an event to know the balance of engery units available for the prosumer before and after renting
  event getOwnerBalance(address prosumerAccount, uint balanceEnergyUnits);
  

  function cleanenergyAvailable(string calldata _engerytype,uint256 _energyUnit) external returns (uint) {
    require (_energyUnit >= 50, 'Not enough energyunits to become active prosumer');

   EnergyProvision memory energyAvailable = EnergyProvision(_engerytype, _energyUnit, true, 8*10**13, msg.sender, 0); 
 
    // Persist `energy` object to the "permanent" storage
    availableEnergy[energyId] = energyAvailable;
    
   ////emiting energy details available with the prosumer
    emit getEnergyDetails(energyId,energyAvailable.energyType, energyAvailable.energyUnit, energyAvailable.prosumer);

    // emit an event to notify the clients
    emit NewEnergy(energyId++);
    
    return energyAvailable.energyUnit;
   
  }
  
   function BuyEnergy(uint _energyId, uint _requiredunits) payable external {
        //test whether consumer has balance in his prosumerAccount
        
    //   uint balance = getConsumerBalance(msg.sender);
    //   require(balance > 0,'Consumer has 0 balance in his account');
       
       
        // check if prosumer of energyID is active
        require(availableEnergy[_energyId].isActive == true,"required units are not available");
        //check if enough balance is available in Consumer ether address to buy required units
        require(msg.value == ( _requiredunits * availableEnergy[_energyId].unitprice),"Insufficient funds");
       //                           40 * 8*10**13
     //check if consumer is buying at least 100 energy unit to confirm the new unitprice as 4*10^13 wei
     //if (_requiredunits.availableEnergy[_energyId] >= 100){unitprice._requiredunits == 4*10**13;}
     
     
        //store billing details 
        Billing memory tempBilling = Billing (
            _energyId,
            _requiredunits,
             msg.sender
            );
        billingDetails[BillingId] = tempBilling;
        BillingId++;
        
        transferToProsumer(_energyId, _requiredunits);
        //send msg to the smartmeter connector to release the energy to the consumer after money is transfered to the prosumer
        
        // updating unitbalance for selling for the prosumer
        availableEnergy[_energyId].unitbalance = (availableEnergy[_energyId].energyUnit - _requiredunits) ;
        if (availableEnergy[_energyId].unitbalance < 50) {
           availableEnergy[_energyId].isActive = false; }
        
    }
    // once time is complete, smart contract will transfer that money to prosuner
    function transferToProsumer(uint _energyId, uint _requiredunits) public {
        //send money to prosumer
        availableEnergy[_energyId].prosumer.transfer((availableEnergy[_energyId].unitprice)*_requiredunits);
      
    }
    //for getting energyunits from energyid
    function getAvailableEnergyUnits(uint256 _energyId) external view returns(uint) {

      EnergyProvision storage prosumerEnergy = availableEnergy[_energyId];
		  return prosumerEnergy.energyUnit;
	}
	
	
	
	//for checking enough balance in consumer prosumerAccount
	function getConsumerBalance(address _consumer) external view returns(uint) {
     		  return  balances[_consumer];
	}
	
	
}
